R2D.RenderUpdater = function() {
    var frames = 5;
    var frame = 0;
    var onStart = null;
    var onProgress = null;
    var onFinish = null;

    function update()
    {
        if ( frame <= 0 )
        {
            if ( onFinish ) {
                onFinish();
            }
            return;
        }

        frame -= 1;

        if (onProgress) {
            onProgress();
        }

        requestAnimationFrame(update);
    }

    this.setFrames = function(number) {
        frames = number;
    };
    this.needsUpdate = function()
    {
        if ( frame > 0 )
        {
            frame = frames;
        } else {
            frame = frames;

            if ( onStart ) {
                onStart();
            }

            update();
        }
    };
    this.onStart = function(callback) {
        onStart = callback;
    };
    this.onProgress = function(callback) {
        onProgress = callback;
    };
    this.onFinish = function(callback) {
        onFinish = callback;
    };
    this.isRunning = function()
    {
        //console.log(frame);
        return frame > 0;
    }
};

