TConf.Tile = function()
{
    var me = this;

    me.material = null;
    me.rotation = 0;
    me.flip = false;
    me.x = 0;
    me.y = 0;

    me.clear = function()
    {
        me.material = null;
        me.rotation = 0;
        me.flip = false;
    };

    me.clone = function()
    {
        var tile = new TConf.Tile();
        tile.material = me.material;
        tile.rotation = me.rotation;
        tile.flip = me.flip;
        tile.x = me.x;
        tile.y = me.y;

        return tile;
    };

    me.randomRotate = function()
    {
        var isSquare = (me.material.width == me.material.height);
        var r = Math.floor(Math.random() * 4);
        switch (r)
        {
            case 0:
                me.rotation = 0;
                break;
            case 1:
                if (isSquare)
                {
                    me.rotation = Math.PI / 2;
                }
                else
                {
                    me.rotation = 0;
                }
                break;
            case 2:
                me.rotation = Math.PI;
                break;
            case 3:
                if (isSquare)
                {
                    me.rotation = Math.PI * 3 / 2;
                }
                else
                {
                    me.rotation = Math.PI;
                }
                break;
        }

        me.flip = Math.random() < 0.5;
    }
};

